    private void jButton1ActionPerformed(java.awt.event.ActionEvent evt) {                                         
        //AQUI VEEE
    	String id_marca = txtID.getText();
    	String nombre_marca = txtNombre.getText();
    	WSConsultaSoapProxy proxy = new WSConsultaSoapProxy();
    	try {
			proxy.insertarMarca(id_marca,nombre_marca);
			JOptionPane.showMessageDialog(null, "Operaci√≥n realizada correctamente", "MENSAJE", JOptionPane.PLAIN_MESSAGE);
		} catch (RemoteException e) {
			JOptionPane.showMessageDialog(null, "Operacion Fallida", "ERROR", JOptionPane.ERROR_MESSAGE);
			e.printStackTrace();
		}
    }                                        

